mapper.select:
SELECT A.BOARD_ID ,A.BOARD_SJ ,A.BOARD_CN ,A.INQIRE_CO ,A.CREAT_IP ,A.NOTICE_AT ,A.OTHBC_AT ,A.USE_AT ,A.ATCH_FILE_ID ,A.FRST_REGIST_PNTTM ,A.FRST_REGISTER_ID FROM BOARD A
ORDER BY A.FRST_REGIST_PNTTM DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO BOARD (

	 BOARD_ID
	 ,BOARD_SJ
	 ,BOARD_CN
	 ,INQIRE_CO
	 ,CREAT_IP
	 ,NOTICE_AT
	 ,OTHBC_AT
	 ,USE_AT
	 ,ATCH_FILE_ID
	 ,FRST_REGIST_PNTTM
	 ,FRST_REGISTER_ID
	 ,LAST_UPDT_PNTTM
	 ,LAST_UPDUSR_ID
) VALUES (
	#{boardId}
	, #{boardSj}
	, #{boardCn}
	, 0
	, #{creatIp}
	, #{noticeAt}
	, #{othbcAt}
	, 'Y'
	, #{atchFileId}
	, NOW()
	, #{userId}
	, NOW()
	, #{userId}
)

mapper.update:

UPDATE BOARD SET
INQIRE_CO = INQIRE_CO+1
WHERE BOARD_ID = #{boardId}

UPDATE BOARD SET
	BOARD_SJ=#{boardSj}
	,BOARD_CN=#{boardCn}
	,NOTICE_AT=#{noticeAt}
	,OTHBC_AT=#{othbcAt}

		,ATCH_FILE_ID=#{atchFileId}

	,LAST_UPDT_PNTTM= NOW()
	,LAST_UPDUSR_ID=#{userId}
WHERE BOARD_ID =#{boardId}




	AND FRST_REGISTER_ID=#{userId}

UPDATE BOARD SET
	USE_AT='N'
WHERE BOARD_ID =#{boardId}




	AND FRST_REGISTER_ID=#{userId}
